import styles from "./CustomerReviews.module.css";
import reviews from "./reviews.json";
import calculateStars from "../../utils/utils";

function CustomerReviews() {
    return (
        <>
            <h3 className="sub-heading">Customer's Reviews</h3>
            <h1 className="heading">what they say</h1>

            <div className={styles.reviewsContainer}>
                <div className={styles.cardReview}>
                    {reviews.map(rev => (
                        <div className={styles.review}>
                            <a href="#">
                                <img src={rev.srcImg} alt="user-photo" />
                            </a>
                            <div class="name-stars-reviews">
                                <h3 className={styles.Name}>{rev.name}</h3>
                                <div class="stars">
                                    {calculateStars()}
                                </div>
                            </div>
                        </div>
                    ))}
                </div>
            </div>
        </>
    )
}

export default CustomerReviews;
