import React, { useState, useEffect } from 'react';
import Logo from "./Logo";
import Navbar from "./NavBar";
import Icons from "./Icons";

function Header() {
    const [isNavBarVisible, setIsNavBarVisible] = useState(window.innerWidth > 768);

    useEffect(() => {
        function handleResize() {
            setIsNavBarVisible(window.innerWidth > 768);
        }

        window.addEventListener('resize', handleResize);

        return () => window.removeEventListener('resize', handleResize);
    }, []);

    function toggleNavBar() {
        setIsNavBarVisible(prevState => !prevState);
    }

    return (
        <>
            {isNavBarVisible ?
                <header>
                    <a href="#top">
                        <Logo />
                    </a>
                    <Navbar isVisible={isNavBarVisible} />
                    <Icons onMenuClick={toggleNavBar} />
                </header>
                :
                <>
                    <header>
                        <a href="#top">
                            <Logo />
                        </a>
                        <Icons onMenuClick={toggleNavBar} onSearchClick={showSearchForm}/>
                    </header>
                    <Navbar isVisible={isNavBarVisible} />
                </>
            }
        </>
    )
}

export default Header;
