function calculateStars(star, halfStar, emptyStar) {
    let starElements = [];

    //for stars:
    for (let i = 0; i < star; i++) {
        starElements.push(<img src="./pictures/star.png" alt="star" />)
    }
    //for half stars:
    for (let i = 0; i < halfStar; i++) {
        starElements.push(<img src="./pictures/star-half-empty.png" alt="half-star" />)
    }
    //for empty stars:
    for (let i = 0; i < emptyStar; i++) {
        starElements.push(<img src="/pictures/empty-star.png" alt="emptyStar"/>)
    }
}

export default calculateStars;
