import react, { useState } from "react;"
import styles from "./searchform.module.css";
import closeIcon from "/pictures/cross.png";

function SearchForm({ isSearchFormVisible }) {
const [isSearchIcon, setIsSearchIcon] = useState(true);

function handleClick() {
    setIsSearchIcon
}
    return (
        <form className={isSearchFormVisible ? styles.active : styles.searchForm}>
            <input type="search" name="" placeholder="search here..." className={styles.searchBox} />
            <label htmlFor="searchBox" className={styles.searchIcon}></label>
            <i className={styles.close} onClick={handleClick} style={{ backgroundImage: `url(${closeIcon})` }}></i>
        </form>
    );
}

export default SearchForm;
