import React from 'react';
import dishes from './specialDishes.json';
import SlideSpecialDish from './SlideSpecialDish';
import { Swiper, SwiperSlide } from 'swiper/react';
import { Autoplay, Pagination } from 'swiper/modules';
import 'swiper/css'; // Core Swiper styles
import 'swiper/css/pagination'; // Pagination module styles
import 'swiper/css/autoplay'; // Autoplay module styles
import styles from "./SpecialDishes.module.css";

function SpecialDishes() {
    return (
        <Swiper
            modules={[Pagination, Autoplay]} // Declare modules to use
            slidesPerView={1}

            loop={true} // Enable looping of slides
            autoplay={{
                delay: 3000, // Delay 3 seconds
                disableOnInteraction: false, // Continue autoplay on interaction
            }}
            pagination={{
                clickable: true, // Make pagination dots clickable
                bulletClass: `swiper-pagination-bullet`, // Default bullet class
                bulletActiveClass: `swiper-pagination-bullet-active`, // Default active bullet class
                modifierClass: `${styles.paginationBullet}`, // Custom modifier class
            }}
            className={styles.swiper}
        >
            {dishes.map(dish => (
                <SwiperSlide key={dish.id}>
                    <SlideSpecialDish
                        id={dish.id}
                        topic={dish.topic}
                        name={dish.name}
                        description={dish.description}
                        img={dish.img}
                    />
                </SwiperSlide>
            ))}
        </Swiper>
    );
}

export default SpecialDishes;
